import React, {useState} from 'react';
import './PostImage.css';

function PostImage(props){
  const listImage = props.images;

  return(
    <div className="container-post-images">
        {listImage.map((image, index) => {
          if(listImage.length === 1){
            return(
                <div className="post_image_item only_image_item_1">
                  <img src={image} alt=""/>
                </div>
            )
          }else if (listImage.length === 2) {
            return(
              <div className="post_image_item only_image_item_1">
                <img src={image} alt=""/>
              </div>
            )
          }else if (listImage.length === 3) {
            return(
              <div className="post_image_item only_image_item_3">
                <img src={image} alt=""/>
              </div>
            )
          }

        })}
    </div>
  );
}


export default PostImage;
