import * as Types from './../Constants/ActionTypes.jsx';

const initialState = [];

export const stories = (state = initialState, action) => {
  switch (action.type) {
    case Types.LIST_STORY:
      return [...state, ...action.stories]
      break;
    default: return [...state]
  }
}
