import * as Types from './../Constants/ActionTypes.jsx';

const initialState = [];

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.POSTS:
      state = [...new Set([...state, action.posts])]
      return state
      break;
    case Types.MORE_POSTS:
      state = action.posts
      return [...state, action.posts]
      break;
    default: return state
  }
}
