import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  loading: true
};

export const onePost = (state = initialState, action) => {
  switch (action.type) {
    case Types.REQUEST_ONE_POST:
      return state
      break;
    case Types.RESPONSE_ONE_POST:
      state.loading = false
      return {...state}
      break;
    default: return state
  }
}
