import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import'./PopupEditPage.css';
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import {PUBLIC_URL} from './../../Constants/public.jsx';

function PopupEditPage(){
  const [lat, setLat] = useState(10.7553411);
  const [long, setLong] = useState(106.4150395);
  const [zoom, setZoom] = useState(5);

  useEffect(() => {

    var map = L.map('map_page').setView([lat, long], zoom);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    var searchControl = L.esri.Geocoding.geosearch().addTo(map);

    var results = L.layerGroup().addTo(map);

    searchControl.on('results', function (data) {
      results.clearLayers();
      for (var i = data.results.length - 1; i >= 0; i--) {
        results.addLayer(L.marker(data.results[i].latlng));
      }
    });
  }, []);

  const getMyAdress = () => {
    navigator.geolocation.getCurrentPosition(getLatLon);
  }

  function getLatLon(position) {
    let latitude = position.coords.latitude;
    let longitude = position.coords.longitude;
  }
  console.log(L);
  return(
    <div className="popup-edit-page">
      <div className="popup-edit-page-container">
        <div className="map-page-container">
          <h5>Chỉnh sửa vị trí cửa hàng <Link to="">ReactJS Việt Nam</Link></h5>
          <div id="map_page" className="map_page"></div>
          <div className="map-page-button">
            <button type="submit" onClick={getMyAdress}>Lấy vị trí của bạn</button>
            <button type="submit">Xác nhận</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PopupEditPage;
