import React, {useState} from 'react';
import {Editor, EditorState} from 'draft-js';
import style from 'styled-components';
import './HomePage.css';
import { FaUserTag, FaImage} from "react-icons/fa";

function HomePage(){

  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty(),
  );

  const StyleEditor = style.div`
    width: 824px;
    max-height: 200px;
    overflow-y: auto;
    margin-left: 10px;
  `;

  return(
    <div className="page-home-container">
      <div className="page-left">
        <div className="create-post">
          <div className="create-post-input">
            <div className="create-post-avatar">
              <img src="http://xemanhdep.com/wp-content/uploads/2016/04/hinh-anh-girl-xinh-dep-nhat-2016-10.jpg" alt="" />
            </div>
            <StyleEditor>
              <Editor  editorState={editorState} onChange={setEditorState} placeholder="Viết bài dì đó"/>
            </StyleEditor>
          </div>
          <div className="create-post-option">
            <div className="create-post-choose-image">

              <label htmlFor="choose-image">
                <FaImage style={{ fontSize: "20px", marginRight: '7px' }} />
                Ảnh/video
              </label>
              <input type="file" id="choose-image" />
            </div>
            <div className="create-post-tag">
              <FaUserTag style={{ fontSize: "20px", marginRight: '7px' }} />
              Gắn thẻ
            </div>
          </div>
        </div>
      </div>
      <div className="page-right">
        right
      </div>
    </div>
  )
}

export default HomePage;
