import React from 'react';
import './PostImage.css';
import { useHistory } from "react-router-dom";
import VideoPlayer from "simple-react-video-thumbnail";
import { FaRegPlayCircle } from "react-icons/fa";
import {Image,Transformation} from 'cloudinary-react';
import {toggleStatusPresentialModal} from './../../Actions/index.jsx';

function PostImage(props){
  const listImage = props.images;

  const as = () => {
    console.log('aaaaa');
  }
  return(
    <div className="container-post-images">
    {listImage.length <= 5 ?
        <>
        {listImage.map((image, index) => {
          if(listImage.length === 1){
            return(
                <div key={index} onClick={() => as()} className={'post_image_item only_image_item_' + index}>
                  {image.post_images_Type === 'image' ?
                    <React.Fragment>
                      {image.post_images_Url.toLowerCase().indexOf('https://') !== -1 ?
                        <img src={image.post_images_Url} alt=""/>
                        :
                        <Image cloudName="mohi-vn" publicId={image.post_images_Url+ ".jpg"} version="1607061343">
                          <Transformation />
                        </Image>
                      }
                    </React.Fragment>
                  :
                  <div className="video-background">
                    <div className="video-background-play"><FaRegPlayCircle /></div>
                    <VideoPlayer videoUrl={image.post_images_Url} />
                  </div>
                  }
                </div>
            )
          }else if (listImage.length === 2) {
            return(
              <div key={index} onClick={() => toggleStatusPresentialModal('modal_share_post', {post: props.post, index: index})} className={'post_image_item two_image_item_' + index}>
                {image.post_images_Type === 'image' ?
                <img src={image.post_images_Url} alt=""/>
                :
                <div className="video-background">
                  <div className="video-background-play"><FaRegPlayCircle /></div>
                  <VideoPlayer videoUrl={image.post_images_Url} />
                </div>
              }
              </div>
            )
          }else if (listImage.length === 3) {
            return(
              <div key={index} onClick={() => toggleStatusPresentialModal('view_image', {post: props.post, index: index})} className={'post_image_item three_image_item_' + index}>
                {image.post_images_Type === 'image' ?
                <img src={image.post_images_Url} alt=""/>
                :
                <div className="video-background">
                  <div className="video-background-play"><FaRegPlayCircle /></div>
                  <VideoPlayer videoUrl={image.post_images_Url} />
                </div>
                }
              </div>
            )
          }else if (listImage.length === 4) {
            return(
              <div key={index} onClick={() => toggleStatusPresentialModal('view_image', {post: props.post, index: index})} className={'post_image_item four_image_item_' + index}>
                {image.post_images_Type === 'image' ?
                <img src={image.post_images_Url} alt=""/>
                :
                <div className="video-background">
                  <div className="video-background-play"><FaRegPlayCircle /></div>
                  <VideoPlayer videoUrl={image.post_images_Url} />
                </div>
                }
              </div>
            )
          }else if (listImage.length === 5) {
            return(
              <div key={index} onClick={() => toggleStatusPresentialModal('view_image', {post: props.post, index: index})} className={'post_image_item five_image_item_' + index}>
                {image.post_images_Type === 'image' ?
                <img src={image.post_images_Url} alt=""/>
                :
                <div className="video-background">
                  <div className="video-background-play"><FaRegPlayCircle /></div>
                  <VideoPlayer videoUrl={image.post_images_Url} />
                </div>
                }
              </div>
            )
          }
        })}
        </>
        :
        <>
        {listImage.slice(0,5).map((image, index) => {
          return(
            <div key={index} onClick={() => toggleStatusPresentialModal('view_image', {post: props.post, index: index})} className={'post_image_item more_image_item_' + index}>
              {image.post_images_Type === 'image' ?
              <img src={image.post_images_Url} alt=""/>
              :
              <div className="video-background">
                <div className="video-background-play"><FaRegPlayCircle /></div>
                <VideoPlayer videoUrl={image.post_images_Url} />
              </div>
              }
              {index === 4 ? <span className="more_image">+{listImage.length - 5}</span> : null}
            </div>
          )
        })}
        </>
      }
        <div className="clear"></div>
    </div>
  );
}


export default PostImage;
