import React, { useState, useRef } from 'react';
import './UploadMuti.css';
import { FiPlus } from "react-icons/fi";
import { AiOutlineClose } from "react-icons/ai";

function UploadMuti(){
  const [files, setFiles] = useState([]);
  const autoScrollToRightRef = useRef();

  const onChange = (e) => {
    if(e.target.files){
      //Tạo list image blob url
      const fileArr = Array.from(e.target.files).map((file) => URL.createObjectURL(file))
      setFiles((previewFile) => previewFile.concat(fileArr))
      autoScrollToRightFunc()
    }
  }

  const autoScrollToRightFunc = () => {
    autoScrollToRightRef.current.addEventListener('DOMNodeInserted', event => {
        const { currentTarget: target } = event;
        target.scroll({ left: target.scrollWidth, behavior: 'smooth' });
      });
  }

  const renderFile = source => {
    return(
    <React.Fragment>
      {source.map((photo, index) => {
        return (
            <div className="list-preview-item" key={index}>
              <img src={photo} />
            <div className="list-preview-item-presential"><AiOutlineClose /></div>
            </div>

        )
      })}
      <div className="list-preview-next-item" >
        <label htmlFor="create_post_upload_image"><FiPlus/></label>
      </div>
    </React.Fragment>
    )
  }

  return(
    <div className="list-preview-container">
      <div className="list-preview-wrap">
        <div className="list-preview" >
          {renderFile(files)}
        </div>
      </div>
      <input id="create_post_upload_image" type="file" multiple onChange={onChange} />
    </div>
  )
}

export default UploadMuti;
