import React, { Suspense, lazy, useEffect, useState } from 'react';
import './Home.css';
import { Link, Route, Switch, useHistory, useLocation } from 'react-router-dom';
import { Layout, Menu, Input, Dropdown, Button } from 'antd';
import { FaUserFriends, FaFacebookMessenger, FaBell } from 'react-icons/fa';
import { AiFillCaretDown } from "react-icons/ai";
import { Spin } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';
import MenuItem from 'antd/lib/menu/MenuItem';
import { ToggleLayer } from "react-laag";
import composeRefs from "./composeRefs.jsx";

const Main = lazy(() => import('./../Components/NewFeed/Index.jsx'));
const MyProfile = lazy(() => import('./../Components/Profiles/MyProfile/Index.jsx'));
const Messenger = lazy(() => import('./../Components/Messenger/index.jsx'));
const SearchNavigation = lazy(() => import('./../Components/Search/index.jsx'));
const FriendRequests = lazy(() => import('./../Components/FriendRequests/FriendRequests.jsx'));
const FriendSent = lazy(() => import('./../Components/FriendRequests/FriendSent.jsx'));

const menu = (
    <Menu>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com/">
                1st menu item
        </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.taobao.com/">
                2nd menu item
        </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.tmall.com/">
                3rd menu item
        </a>
        </Menu.Item>
        <MenuItem>
            <Link to="/friendrequests"> abc</Link>
        </MenuItem>
    </Menu>
);

const PopoverMenu = React.forwardRef(function PopoverMenu(props, ref) {
    return (
      <ToggleLayer
        ResizeObserver={ResizeObserver}
        renderLayer={(props) => {
          function handleClick(item) {
            return function onClick() {
              alert(`You clicked on "${item}"`);
              props.close();
            };
          }
  
          return (
            <>
              {props.isOpen ? (
                <Menu
                  ref={props.layerProps.ref}
                  style={props.layerProps.style}
                  arrowStyle={props.arrowStyle}
                  layerSide={props.layerSide}
                >
                  <MenuItem onClick={handleClick("Item 1")}>Item 1</MenuItem>
                  <MenuItem onClick={handleClick("Item 2")}>Item 2</MenuItem>
                  <MenuItem onClick={handleClick("Item 3")}>Item 3</MenuItem>
                  <MenuItem onClick={handleClick("Item 4")}>Item 4</MenuItem>
                </Menu>
              ) : null}
            </>
          );
        }}
        closeOnOutsideClick
        closeOnDisappear="partial"
        placement={{
          anchor: "BOTTOM_CENTER",
          autoAdjust: true,
          snapToAnchor: false,
          triggerOffset: 12,
          scrollOffset: 16,
          preferX: "RIGHT"
        }}
      >
        {({ isOpen, triggerRef, toggle }) => (
          <Button
            ref={composeRefs(triggerRef, ref)}
            onClick={toggle}
            style={props.style}
          >
            {isOpen ? "Hide" : "Show"}
          </Button>
        )}
      </ToggleLayer>
    );
  });

const menufriendrequests = (
    <Menu className="friendrequests-menu-container">
        <div className="friendrequests-menu-title">
            <h5 aria-disabled>Lời mời kết bạn</h5>
            <Link to="/friendrequests"><a> Tìm thêm bạn bè</a></Link>
            <a>Cài đặt</a>
        </div>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com/">
                1st menu item
        </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.taobao.com/">
                2nd menu item
        </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.tmall.com/">
                3rd menu item
        </a>
        </Menu.Item>
        <span type="primary">
            <Link to="/friendrequests"><a> Xem tất cả các yêu cầu</a></Link>
        </span>
    </Menu>
);

function Home() {
    const { Header } = Layout;
    const { Search } = Input;
    const history = useHistory();
    const path = useLocation().pathname.split('/');
    const key = history.location.key;
    const isActive = path[1];
    const [loadingContent, setLoadingContent] = useState(true);
    const antIcon = <LoadingOutlined style={{ fontSize: 50, color: 'rgba(0,128,128)' }} spin />;
    const [element, toggleLayerProps] = useToggleLayer(
        ({ menufriendrequests, isOpen }) => isOpen && <div {...menufriendrequests} />,
        { placement: { anchor: anchor.BOTTOM_CENTER } }
    );
        console.log()
    useEffect(() => {
        window.scrollTo(0, 0)
        setLoadingContent(true)

        setTimeout(() => {
            setLoadingContent(false)
        }, 200);

    }, [key]);

    function onSearch(value) {
        history.push('/search/' + value)
    }
    return (
        <>
            <Layout className="layout " >
                <Header className="nav-home">
                    <div className="container d-flex header-home " >
                        <div className="logo " >
                            <img src={"logo.png"} alt="Mohi.vn" title="Mohi.vn" />
                        </div>
                        <Search
                            className="search-all"
                            placeholder="Tìm kiếm"
                            onSearch={(value) => onSearch(value)}
                            enterButton
                        />
                        <Menu className="menu-home" mode="horizontal" >
                            <Menu.Item className="after-li">
                                <Link className="avatar-home" to="/profile" >
                                    <img src="images/gaixinh.jpg" alt="" />
                                    <span>Phước</span>
                                    {/*  */}
                                </Link>
                            </Menu.Item>
                            <Menu.Item className="after-li">
                                <Link className="home" to="/"  > Trang chủ</Link>
                            </Menu.Item>
                            <Menu.Item>
                                <PopoverMenu />
                            </Menu.Item>
                            <Menu.Item >
                                <Dropdown className="dropdown-setting" trigger={['click']} overlay={menufriendrequests} placement="bottomCenter" arrow>
                                    <FaUserFriends title="Bạn bè" />
                                </Dropdown>
                            </Menu.Item>
                            <Menu.Item >
                                <Dropdown className="dropdown-setting" trigger={['click']} overlay={menu} placement="bottomCenter" arrow>
                                    <FaFacebookMessenger title="Tin nhắn" />
                                </Dropdown>
                            </Menu.Item>
                            <Menu.Item >
                                <Dropdown className="dropdown-setting" trigger={['click']} overlay={menu} placement="bottomCenter" arrow>
                                    <FaBell title="Thông báo" />
                                </Dropdown>
                            </Menu.Item>
                            <Menu.Item >
                                <Dropdown className="dropdown-setting" trigger={['click']} overlay={menu} placement="bottomCenter" arrow>
                                    <AiFillCaretDown title="Cài đặt" />
                                </Dropdown>
                            </Menu.Item>
                        </Menu>
                    </div>
                </Header>
                <Suspense fallback={(<div></div>)}>
                    <Switch>
                        {loadingContent ? <div className="spin"> <Spin indicator={antIcon} /></div> :
                            <>
                                <Route exact path="/" component={Main} />
                                <Route path="/profile" component={MyProfile} />
                                <Route path="/messenger" component={Messenger} />
                                <Route path="/friendrequests" component={FriendRequests} />
                                <Route path={`/search/:value`} component={SearchNavigation} />
                                <Route path="/friend-sent" component={FriendSent} />

                            </>
                        }
                    </Switch>
                </Suspense>
            </Layout>
        </>
    )
}

export default Home;
