import React from 'react';
import 'antd/dist/antd.css';
import './Login.css';
import {Layout, Form, Input, Button} from 'antd';
import {UserOutlined, LockOutlined} from '@ant-design/icons';
import {PUBLIC_URL} from './../../Constants/public.jsx';
import {useForm} from 'react-hook-form';

function Login() {
  const {Header, Content, Footer} = Layout;
  const dates = [];
  const months = [];
  const years = [];
  const {register, handleSubmit, errors} = useForm();

  const onSubmit = (data) => {
    console.log(data);
  }
  for (let i = 1; i <= 31; i++) {
    dates.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 1; i <= 12; i++) {
    months.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 0; i <= 60; i++) {
    const year = (new Date()).getFullYear() - i;
    years.push(<option key={i} value={year}>{year}</option>);
  }

  return (<Layout className="layout layout-login">
    <Header className="header-login">
      <div className=" header-login-container" style={{
          alignItems: 'center',
          position: 'relative'
        }}>
        <div className="logo">
          <span>Mohi</span>
        </div>
        <Form name="horizontal_login" layout="inline" style={{
            position: 'absolute',
            right: 0
          }}>
          <Form.Item name="username">
            <Input name="login_username" prefix={<UserOutlined className = "site-form-item-icon" />} placeholder="Tài khoản"/>
          </Form.Item>
          <Form.Item name="password">
            <Input name="login_password" prefix={<LockOutlined className = "site-form-item-icon" />} type="password" placeholder="Mật khẩu"/>
          </Form.Item>
          <Form.Item>
            <button type="submit" className="button-login">
              Đăng nhập
            </button>
          </Form.Item>
        </Form>
      </div>
    </Header>
    <Content style={{
        padding: '0 50px'
      }}>
      <div className="site-layout-content">
        <div className="site-layout-viet-nam">
          <img src={PUBLIC_URL + `/vietnam.png`} alt="Bản đồ Việt Nam"/>
        </div>
        <div className="site-layout-register">
          <div className="site-layout-register-container">
            <h2>Chào mừng bạn trở lại với Mohi</h2>
            <h4>Đăng kí</h4>
            <form onSubmit={handleSubmit(onSubmit)}>
              <div className="site-layout-register-group">
                <div className="register-first-name">
                  <label htmlFor="register_first_name">Họ</label>
                  <input id="register_first_name" name="register_first_name" type="text" placeholder="Họ..." ref={register}/>
                </div>
                <div className="register-last-name">
                  <label htmlFor="register_last_name">Tên</label>
                  <input id="register_last_name" name="register_last_name" type="text" placeholder="Tên..." ref={register}/>
                </div>
              </div>
              <div className="site-layout-register-group">
                <div className="register-email">
                  <label htmlFor="register_email">Số điện thoại di động hoặc email</label>
                  <input id="register_email" name="register_email" type="text" placeholder="Số điện thoại di động hoặc email..." ref={register}/>
                </div>
              </div>
              <div className="site-layout-register-group">
                <div className="register-birthday">
                  <label htmlFor="register_birthday">Ngày sinh</label>
                  <select id="register_birthday_date" name="register_birthday_date" ref={register}>
                    {dates}
                  </select>
                  <select id="register_birthday_month" name="register_birthday_month" ref={register}>
                    {months}
                  </select>
                  <select id="register_birthday_year" name="register_birthday_year" ref={register}>
                    {years}
                  </select>
                </div>
              </div>
              <div className="site-layout-register-group">
                <label htmlFor="register_sex">Giới tính</label>
                <div className="register-sex">
                  <input id="register_sex_men" name="register-sex" value="nam" type="radio" ref={register}/>
                  <label htmlFor="register_sex_men">Nam</label>
                </div>
                <div className="register-sex">
                  <input id="register_sex_women" name="register-sex" value="nữ" type="radio" ref={register}/>
                  <label htmlFor="register_sex_women">Nữ</label>
                </div>
                <div className="register-sex">
                  <input id="register_sex_lt" name="register-sex" value="other" type="radio" ref={register}/>
                  <label htmlFor="register_sex_lt">Không xác định</label>
                </div>
              </div>
              <div className="site-layout-register-group">
                <button>Đăng kí</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </Content>
  </Layout>)
}

export default Login;
