import React, { useState } from 'react';

function UploadMuti(){
  const [files, setFiles] = useState([]);

  const onChange = (e) => {
    e.target.files.map(file => {
      setFiles([...files, file])
    })
  }

  console.log(files);
  return(
    <input type="file" multiple onChange={onChange} />
  )
}

export default UploadMuti;
