import React, {useState} from 'react';
import './PostImage.css';
import { useHistory } from "react-router-dom";

function PostImage(props){
  const listImage = props.images;
  const history = useHistory();
  function showModalPostImage(id){
      history.push('/post_modal/'+id)
  }
console.log(props);
  return(
    <div className="container-post-images">
    {listImage.length <= 5 ?
        <>
        {listImage.map((image, index) => {
          if(listImage.length === 1){
            return(
                <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item only_image_item_' + index}>
                  <img src={image} alt=""/>
                </div>
            )
          }else if (listImage.length === 2) {
            return(
              <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item two_image_item_' + index}>
                <img src={image} alt=""/>
              </div>
            )
          }else if (listImage.length === 3) {
            return(
              <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item three_image_item_' + index}>
                <img src={image} alt=""/>
              </div>
            )
          }else if (listImage.length === 4) {
            return(
              <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item four_image_item_' + index}>
                <img src={image} alt=""/>
              </div>
            )
          }else if (listImage.length === 5) {
            return(
              <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item five_image_item_' + index}>
                <img src={image} alt=""/>
              </div>
            )
          }
        })}
        </>
        :
        <>
        {listImage.slice(0,5).map((image, index) => {
          return(
            <div key={index} onClick={() => showModalPostImage(props.id)} className={'post_image_item more_image_item_' + index}>
              <img src={image} alt=""/>
              {index === 4 ? <span className="more_image">+{listImage.length - 5}</span> : null}
            </div>
          )
        })}
        </>
      }
        <div className="clear"></div>
    </div>
  );
}


export default PostImage;
