import React, {useRef} from 'react';
import './Post.css';
import PostImage from './PostImage.jsx';
import CommentEditor from './CommentEditor.jsx';
import {Link} from 'react-router-dom';
import { AiOutlinePlus, AiOutlineLike } from "react-icons/ai";
import { BsThreeDots } from "react-icons/bs";
import { FaRegCommentAlt } from "react-icons/fa";
import { IoIosShareAlt } from "react-icons/io";
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
import { connect } from 'react-redux';

function PostPage(props){
  const inputCommentRef = useRef();

  const imagePost = [
  'https://soikeom88.com/wp-content/uploads/2019/02/gai-xinh-thai-lan-guong.jpg',
  'https://2.bp.blogspot.com/-PsEM9GLEN-c/UeAs9CySGPI/AAAAAAAAE5w/vBKIjcHaywU/s640/hinh-anh-gai-xinh-hd-taihinhnendep.com-5.jpg',
  'http://file.vforum.vn/hinh/2016/04/girl-xinh-gai-dep-2016-2.jpg',
  'https://img2.thuthuatphanmem.vn/uploads/2019/02/22/hinh-nen-gai-xinh-2k-cho-pc_121750528.jpg',
  'http://hinhnendepnhat.net/wp-content/uploads/2016/04/hinh-anh-gai-xinh-de-thuong-mat-long-lanh-1.jpg',
  'https://thuthuatnhanh.com/wp-content/uploads/2019/12/hinh-anh-gai-xinh-de-thuong-13.jpg',
  "https://i.9mobi.vn/cf/images/2015/03/nkk/hinh-nen-girl-xinh-15.jpg",
  "https://tse2.mm.bing.net/th?id=OIP.e6j5IOw-7mcLVaU-1aONSwHaJ3&pid=Api&P=0&w=300&h=300",
  "https://picsum.photos/id/1015/1000/600/",
  "https://picsum.photos/id/1015/250/150/",
  "https://picsum.photos/id/1019/1000/600/",
  ];

  const { loading, error, data } = useQuery(
    gql`query {
      CallingCode {
        name
        countries {
          name
        }
      }
    }`
  );
  console.log(data);
  function focusInputComment(){
    let inputRef = inputCommentRef.current;
    inputRef.focusCommentInput();
  }


  if (loading) return <p>loading...</p>
  if (error) return <p>{error.message}</p>;
  return(
    <div className="post">
      <div className="no-post">
        <div className="arrow-no-post" onClick={props.showModalCreatePostFunc}>
          <AiOutlinePlus />
        </div>
        <p>Tạo bài viết mới</p>
      </div>
      <div className="post-container">
        <div className="post-header">
          <div className="post-info">
            <div className="post-avatar">
            <Link to="">
              <img src="https://tse2.mm.bing.net/th?id=OIP.e6j5IOw-7mcLVaU-1aONSwHaJ3&pid=Api&P=0&w=300&h=300" alt="" />
            </Link>
            </div >
            <div className="post-boss">
              <p><Link to="">Nguyễn Văn Phước</Link></p>
              <p>1 giờ trước</p>
            </div>
          </div>
          <div className="post-setting">
            <BsThreeDots />
          </div>
        </div>
        <div className="post-body">
          <div className="post-content">
          aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
          </div>
          <PostImage imagePost={imagePost} />
        </div>
        <div className="post-footer">
          <div className="post-total-action">
            <span>Hồ Thị Vân Anh và 156 người khác đã thích bài viết này</span>
          </div>
          <div className="post-action">
            <div className="post-action-item">
              <AiOutlineLike />
            </div>
            <div className="post-action-item" onClick={() => focusInputComment()}>
              <FaRegCommentAlt />
            </div>
            <div className="post-action-item">
              <IoIosShareAlt />
            </div>
          </div>
        </div>
        <div className="post-comment">
          <div className="post-comment-current-user">
            <img src="https://picsum.photos/id/1019/1000/600/" alt="" />
          </div>
          <div className="post-comment-input">
            <CommentEditor ref={inputCommentRef}/>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PostPage;
