import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  loading:true,
  list : []
};

export const friends = (state = initialState, action) => {
  switch (action.type) {
    case Types.REQUEST_LIST_FRIEND:
      return {
        ...state
      }
      break;
    case Types.RESPONSE_LIST_FRIEND:
      return {
        loading: !state.loading,
        list: [...action.friends]
      }
      break;
    default: return [...state]
  }
}
