import React, {useState} from 'react';
import {useHistory} from 'react-router-dom';
var CryptoJS = require("crypto-js");

function VeriCode() {
  const history = useHistory();
  const code_encrypt = history.location.state.code;
  if (!code_encrypt) {
    history.push('/')
  }
  //  Giải mã code
  let bytes = CryptoJS.AES.decrypt(code_encrypt, 'code_veri');
  let code = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
  console.log(code);
  const [message, setMessage] = useState(history.location.state.message);
  const [veriCode, setVeriCode] = useState();

  const onChange = (e) => {
    setVeriCode(e.target.value)
  }

  const onSubmit = () => {}

  return (<div>
    <form onSubmit={onSubmit}>
      {
        message
          ? <div className="message">{message}</div>
          : null
      }}
      <div className="form-veri-code-group">
        <input onChange={onChange} type="text" placeholder="Nhập mã gồm 6 số..."/>
      </div>
      <div className="form-veri-code-group">
        <button type="submit">Xác nhận</button>
      </div>
    </form>
  </div>)
}

export default VeriCode;
