import React, { Component } from "react";
import Editor, { createEditorStateWithText } from "draft-js-plugins-editor";
import createEmojiPlugin from "draft-js-emoji-plugin";
import "draft-js-emoji-plugin/lib/plugin.css";
import style from 'styled-components';
import { EditorState, Modifier, ContentState, convertToRaw } from 'draft-js';
import htmlToDraft from 'html-to-draftjs';
import createLinkifyPlugin from 'draft-js-linkify-plugin';
import draftToHtml from 'draftjs-to-html';

const emojiPlugin = createEmojiPlugin({
  useNativeArt: true
});
const linkifyPlugin = createLinkifyPlugin();

const { EmojiSuggestions, EmojiSelect } = emojiPlugin;
const plugins = [emojiPlugin, linkifyPlugin];

const StyleEditor = style.div`
  width: 100%;
  max-height: 100px;
  overflow-y: auto;
  border: 1px solid #ddd;
  margin-left: 10px;
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 30px;
`;

const Wrapper = style.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const StyleEmoji = style.div`
  position: absolute;
  right: 10px;
  border: 0;
  z-index: 2;
`;

export default class CommentReplyEditor extends Component {
    state = {
      editorState: EditorState.createEmpty(),
      idUserReceivedReply: this.props.idUserReceivedReply,
      userReceivedReply: this.props.userReceivedReply
    };



    componentDidUpdate() {
      console.log(this.props);
     if (this.props.idUserReceivedReply !== this.state.idUserReceivedReply || this.props.userReceivedReply !== this.state.userReceivedReply) {
       this.setState({
         idUserReceivedReply: this.props.idUserReceivedReply,
         userReceivedReply: this.props.userReceivedReply,
         editorState: insertText(this.props.userReceivedReply, this.state.editorState)
       });

       const insertText = (userReceivedReply, editorState) => {
           const currentContent = this.state.editorState.getCurrentContent(),
                 currentSelection = this.state.editorState.getSelection();
            const linkUserReceivedReply = "<a href='http://localhost:3000/page' ><strong>"+ userReceivedReply +"</strong></a>"
            let { contentBlocks, entityMap } = htmlToDraft(linkUserReceivedReply);

           const newContent = Modifier.replaceWithFragment(
             currentContent,
             currentSelection,
             ContentState.createFromBlockArray(contentBlocks, entityMap).getBlockMap()
           );

           const newEditorState = EditorState.push(this.state.editorState, newContent, 'insert-user-reiceived-reply');
           return  EditorState.forceSelection(newEditorState, newContent.getSelectionAfter());
         }


     }
     }

  onChange = (editorState) => {
    this.setState({
      editorState
    });
  };

  render() {
    console.log(draftToHtml(convertToRaw(this.state.editorState.getCurrentContent())));
    return (
      <Wrapper>
        <StyleEditor>
          <Editor
            editorState={this.state.editorState}
            onChange={this.onChange}
            plugins={plugins}
            placeholder="Viết bình luận..."
          />
        </StyleEditor>
        <EmojiSuggestions />
        <StyleEmoji>
          <EmojiSelect />
        </StyleEmoji>
      </Wrapper>
    );
  }
}
