import React, { useState } from 'react';
import './UploadMuti.css';

function UploadMuti(){
  const [files, setFiles] = useState([]);

  const onChange = (e) => {
    if(e.target.files){
      //Tạo list image blob url
      const fileArr = Array.from(e.target.files).map((file) => URL.createObjectURL(file))
      setFiles((previewFile) => previewFile.concat(fileArr))
    }
  }

  const renderFile = source => {
    return(
    <React.Fragment>
      {source.map((photo, index) => {
        return (
            <div className="list-preview-item" key={index}>
              <img src={photo} />
            </div>

        )
      })}
      <div className="list-preview-next-item">
        <label htmlFor="create_post_upload_image"><TiPlus/></label>
      </div>
    </React.Fragment>
    )
  }

  return(
    <div>
      <input id="create_post_upload_image" type="file" multiple onChange={onChange} />
      <div className="list-preview-wrap">
        <div className="list-preview">
          {renderFile(files)}
        </div>
      </div>
    </div>
  )
}

export default UploadMuti;
