import React from 'react';
import 'antd/dist/antd.css';
import './Login.css';
import './Register.jsx';
import {Layout, Form, Input, Button} from 'antd';
import {UserOutlined, LockOutlined} from '@ant-design/icons';
import {PUBLIC_URL} from './../../Constants/public.jsx';
import {useForm} from 'react-hook-form';

function Login() {
  const {Header, Content, Footer} = Layout;
  const dates = [];
  const months = [];
  const years = [];
  const {login, handleSubmitLogin, errorsLogin} = useForm();

  const onSubmit = (data) => {
    console.log(data);
  }
  for (let i = 1; i <= 31; i++) {
    dates.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 1; i <= 12; i++) {
    months.push(<option key={i} value={i}>{i}</option>);
  }

  for (let i = 0; i <= 60; i++) {
    const year = (new Date()).getFullYear() - i;
    years.push(<option key={i} value={year}>{year}</option>);
  }

  return (<Layout className="layout layout-login">
    <Header className="header-login">
      <div className=" header-login-container" style={{
          alignItems: 'center',
          position: 'relative'
        }}>
        <div className="logo">
          <span>Mohi</span>
        </div>
        <form onSubmit={handleSubmit(onSubmit)} name="login">
          <div className="form-login-group" name="username">
            <input name="login_username" placeholder="Tài khoản" ref={login}/>
          </div>
          <div className="form-login-group" name="password">
            <input name="login_password" type="password" placeholder="Mật khẩu" ref={login}/>
          </div>
          <div className="form-login-group">
            <button type="submit" className="button-login">
              Đăng nhập
            </button>
          </div>
        </form>
      </div>
    </Header>
    <Content style={{
        padding: '0 50px'
      }}>
      <div className="site-layout-content">
        <div className="site-layout-viet-nam">
          <img src={PUBLIC_URL + `/vietnam.png`} alt="Bản đồ Việt Nam"/>
        </div>
        <div className="site-layout-register">
          <Register/>
        </div>
      </div>
    </Content>
  </Layout>)
}

export default Login;
