import * as Types from './../Constants/ActionTypes.jsx';

const initialState = [
  {
    id : 1,
    name : 'test1',
    postBoss: 'Nguyễn Văn Phước',
    created: 'test'
  },
];

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.POSTS:
      state = action.posts
      return [...state]
      break;
    default: return [...state]
  }
}
