import axios from 'axios';
import {API_REFRESH_TOKEN} from './../Constants/Api.jsx';

export const interceptorAxios = (token) => {
  axios.interceptors.request.use(
     config => {
         if (token) {
             config.headers['Authorization'] = 'Bearer ' + token;
         }
         return config;
     },
     error => {
         Promise.reject(error)
   });


  axios.interceptors.response.use((response) => {
     return response
  },
  function (error) {
     const originalRequest = error.config;
     if (error.response.status === 401 && !originalRequest._retry) {

         originalRequest._retry = true;
         return new Promise(async (resolve, reject) => {
            await axios.post(API_REFRESH_TOKEN,
               {
                   'token' : token
               })
               .then((res) => {
                   if (res.status === 200) {
                       localStorage.setItem('ustk', JSON.stringify(res.data));
                       const new_token = JSON.parse(localStorage.getItem('ustk')).access_token;
                       console.log(new_token);
                       axios.defaults.headers.common['Authorization'] = 'Bearer ' + new_token;
                       originalRequest.headers['Authorization'] = 'Bearer ' + new_token;
                       resolve(axios(originalRequest));
                   }
               })
          })
     }
     return Promise.reject(error);
  });
}
