import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  loading:true,
  list : []
};

var findItem = (arr, id) => {
  var result = {
    index: -1,
    item: ''
  };

  arr.forEach((item, i) => {
    if(item.id === id){
      result.index = i;
      result.item = item;
    }
  });

  return result;
}

export const message = (state = initialState, action) => {
  switch (action.type) {
    case Types.REQUEST_MESSAGE_POPUP:
      return {
        ...state
      }
      break;
    case Types.RESPONSE_MESSAGE_POPUP:
      return {
        loading: !state.loading,
        list: action.payload
      }
      break;
    case Types.ADD_NEW_MESSAGE_OF_LIST:
      var mess = findItem(state.list, action.payload.to.id);
      console.log(mess);
      return {...state}
      break;
    default: return {...state}
  }
}
