import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  loading:true,
  loadingfriendOfProfile:true,
  list : [],
  friendOfProfile: []
};

export const friends = (state = initialState, action) => {
  switch (action.type) {
    case Types.REQUEST_LIST_FRIEND:
      return {
        ...state
      }
      break;
    case Types.RESPONSE_LIST_FRIEND:
      return {
        ...state,
        loading: !state.loading,
        list: action.payload
      }
      break;
    case Types.REQUEST_FRIEND_OF_PROFILE:
      return {
        ...state
      }
      break;
    case Types.RESPONSE_FRIEND_OF_PROFILE:
      return {
        ...state,
        loadingfriendOfProfile: !state.loadingfriendOfProfile,
        friendOfProfile: action.payload
      }
      break;
    case Types.SEARCH_FRIEND_OF_PROFILE:
      state.friendOfProfile = state.friendOfProfile.filter(fr => {
        const name = fr.user_first_name + ' ' + fr.user_last_name;
        console.log(name.toLowerCase().indexOf(action.payload.toLowerCase()));
        return name.toLowerCase().indexOf(action.payload.toLowerCase()) !== -1
      })
      return {
        ...state
      }
      break;
    default: return {...state}
  }
}
