import React, {useEffect} from 'react';
import NewFeed from './../Components/NewFeed/NewFeed.jsx';
import axios from 'axios';
import {useSelector, useDispatch} from 'react-redux';
import { fetchPosts } from './../Actions/index.jsx';
import { responsePosts } from './../Actions/index.jsx';

function NewFeedContainer({posts}){
  const { currentPage } = useSelector(state => state.posts);

  const getPostNewFeed = () => {
        axios({
          method: 'GET',
          url: "/api/auth/get-post-home",
          params: {page : currentPage}
        })
        .then(res =>{
          dispatch(responsePosts(res.data))
        })
        .catch(e => {
          if(axios.isCancel(e)) return
        })
  }
  console.log(currentPage);
  useEffect(() => {
    dispatch(fetchPosts())
    getPostNewFeed();
  }, [currentPage])

  const dispatch = useDispatch();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getApiYourGroup()
  }, [])

  const getApiYourGroup = async () => {
      await axios.get('/api/auth/my-groups').then((res) => {
        dispatch(fetchMyGroups(res.data))
      })
      setLoading(false)
  }

  return(
    <NewFeed groups={groups} loading={loading}/>
  )
}
export default NewFeedContainer;
