import React, {useState} from 'react';
import CreatePost from './../Components/NewFeed/CreatePost.jsx';
import axios from 'axios';

function CreatePostContainer(){
  const [loading, setLoading] = useState(false);

  const submitCreatePost = async (valueEditor, fileList, privacyNewFeed, privacyStory, placeNewFeed, placeStory) => {
    setLoading(true)
    console.log(valueEditor, fileList, privacyNewFeed, privacyStory, placeNewFeed, placeStory);
    let data = {
      placeStory: placeStory,
      placePost: placeNewFeed,
      post_Content: valueEditor,
      post_Privacy: privacyNewFeed,
      tag_users_UserId: '',
      story_Privacy: privacyStory,
      post_images_Url: fileList
    }
    await axios.post('/api/auth/create-post-profile', data).then((res) => {
      setLoading(false)
      console.log(res);
    }).catch((e) => {
      console.log(e);
    })
  }

  return(
    <CreatePost submitCreatePost={submitCreatePost} loading={loading}/>
  )
}

export default CreatePostContainer;
