import React from 'react';
import ForgotPassword from './../app/Authen/ForgotPassword.jsx';
import axios from 'axios';
import {API_FORGOT_PASSWORD} from './../Constants/Api.jsx';
import { useDispatch } from 'react-redux';
import { toggleStatusSpin } from './../Actions/index.jsx';

function ForgotPasswordContainer(){
  const dispatch = useDispatch();

  const onSubmitForgotPass = async (data) => {
    console.log(data);
    await axios.post('/api/forgot-password', {action: 'forget_password', user_email: data.user_email})
    .then((res) => {
      console.log(res);
    })
    .catch((err) => {
      console.log(err);
    })
  }

  return(
    <ForgotPassword onSubmitForgotPass={onSubmitForgotPass}/>
  )
}

export default ForgotPasswordContainer;
