import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  loading: true
};

export const onePost = (state = initialState, action) => {
  switch (action.type) {
    case Types.STATUS_SPIN:
      return state
      break;
    case Types.TOGGLE_STATUS_SPIN:
      state.status = !state.status
      return {...state}
      break;
    default: return state
  }
}
