import React, { useState } from 'react';

function UploadMuti(){
  const [files, setFiles] = useState([]);
  const [fileArr, setFilefilesArr] = useState([]);

  const onChange = (e) => {
      setFiles([...files, e.target.files])
  }

  const uploadFile = () => {
    for (let i = 0; i < setFiles.length; i++) {
      setFilefilesArr([...fileArr, files[i]]);
    }
  }

  console.log(files);
  return(
    <input type="file" multiple onChange={onChange} />
  )
}

export default UploadMuti;
