import React, {useEffect, useState} from 'react';
import NewFeed from './../Components/NewFeed/Newfeed.jsx';
import axios from 'axios';
import {useDispatch} from 'react-redux';
import { fetchPosts } from './../Actions/index.jsx';
import { responsePosts } from './../Actions/index.jsx';

function NewFeedContainer(){
  const { currentPage, listPosts, loading, error, hasMore } = useSelector(state => state.posts);
  const pageDefault = 1;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPosts())
    getPostNewFeed();
  }, [currentPage])

  const getPostNewFeed = () => {
        axios({
          method: 'GET',
          url: "/api/auth/get-post-home",
          params: {page : currentPage}
        })
        .then(res =>{
          dispatch(responsePosts(res.data))
        })
        .catch(e => {
          if(axios.isCancel(e)) return
        })
  }

  return(
    <NewFeed />
  )
}
export default NewFeedContainer;
