import React, {useState} from 'react';
import './ModalAddWorkSpace.css';
import { useDispatch, useSelector } from 'react-redux';
import { toggleStatusPresentialModal } from './../../Actions/index.jsx';
import { useForm } from "react-hook-form";
import { responseUpdateProfile } from './../../Actions/index.jsx';
import { requestUpdateProfile } from './../../Actions/index.jsx';
import editInfo from './../../axios/axiosEditInfoProfile.jsx';
import { WaveLoading } from 'react-loadingg';
import axios from 'axios';
import {RiErrorWarningLine} from 'react-icons/ri';
import {Tooltip} from 'antd';

function ModalEditEmail({ presentialModal, profile }) {
    const statusPresentialModal = useSelector(state => state.presentialModal);
    const {loadingUpdateProfile} = useSelector(state => state.profile);
    const [isCheckOtp, setIsCheckOtp] = useState(false);
    const [otp, setOtp] = useState();
    const [formData, setFormData] = useState(false);
    const [loadingOtp, setLoadingOtp] = useState(false);
    const dispatch = useDispatch();
    const { register, handleSubmit, watch, errors } = useForm();

    const onSubmitVery = (data) => {
      if(otp === data.otp){
        dispatch(requestUpdateProfile())

        editInfo({type: 'user_email', value: formData.user_email})
        .then(res => {
          dispatch(responseUpdateProfile({type: 'user_email', value: formData.user_email}))
          dispatch(toggleStatusPresentialModal())
        })
      }
    }

    const onSubmit = (data) => {
      setLoadingOtp(true)

      axios.post("https://www.api.mohi.vn/api/auth/send-otp-add", {user_email:data.user_email, action: 'update'})
      .then((res) => {
        setOtp(res.data.veri_code)
        setFormData(data)
        setIsCheckOtp(true)
      })
      .catch((e) => {
        if(e.response.status === 400){

        }
      })
      setLoadingOtp(false)
    }

    const validateInputEmail = (value) => {
      /* Email Test */
      var filter = /^[\w.+\-]+@gmail\.com$/;
      if (filter.test(value)) {
        return true;
      }
      return false;
    }


    if(isCheckOtp){
        return(
          <React.Fragment>
              <div className="modal-custom modal-add-work-space">
                  <div className="modal-add-work-space-header">
                      Xác nhận mã
                  </div>
                  <form onSubmit={handleSubmit(onSubmitVery)}>
                      <div className="modal-add-work-space-main">
                          <div className="modal-add-work-space-main-input">
                              <label htmlFor="">Nhập mã xác nhận bạn nhận được</label>
                            <input type="text" name="otp" defaultValue="aaaaaaaa" ref={register} />
                          </div>

                      </div>
                      <div className="modal-add-work-space-footer">
                        {loadingUpdateProfile ?
                          <button disabled type="submit">Xác nhận</button>
                          :
                          <button type="submit">Xác nhận</button>
                        }
                      </div>
                  </form>
                  { loadingUpdateProfile && <div style={{background: 'rgba(255,255,255,0.7)', position: 'absolute', width: '100%', height: '100%', top: 0, left: 0}}><WaveLoading /></div>}
              </div>
          </React.Fragment>
        )
    }

    return (
        <React.Fragment>
            <div className="modal-custom modal-add-work-space">
                <div className="modal-add-work-space-header">
                    Chỉnh sửa email của bạn
                </div>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="modal-add-work-space-main">
                        <div className="modal-add-work-space-main-input">
                            <label htmlFor="">Email của bạn</label>
                          <input type="text" name="user_email" id="" defaultValue={statusPresentialModal.data} ref={register({
                              validate: value => validateInputEmail(value)
                            })} />
                          {errors.user_email && <Tooltip placement="bottom" title={<p className = "error_sex_tooltip" >
                              Email không đúng định dạng
                              </p>}><RiErrorWarningLine className="error_sex"/></Tooltip>
                          }
                        </div>

                    </div>
                    <div className="modal-add-work-space-footer">
                      {loadingUpdateProfile ?
                        <button disabled type="submit">Xác nhận</button>
                        :
                        <button type="submit">Xác nhận</button>
                      }
                        <button onClick={ () => dispatch(toggleStatusPresentialModal())} >Hủy</button>
                    </div>
                </form>
                { loadingOtp && <div style={{background: 'rgba(255,255,255,0.7)', position: 'absolute', width: '100%', height: '100%', top: 0, left: 0}}><WaveLoading /></div>}
            </div>
        </React.Fragment>
    )
}

export default ModalEditEmail;
