import React, {useEffect, useRef, useState} from 'react';
import {Col, Row} from 'antd';
import { GiEarthAmerica } from "react-icons/gi";
import { BsFillImageFill } from "react-icons/bs";
import { FaUserFriends } from "react-icons/fa";
import { Link } from 'react-router-dom';
import { GoPrimitiveDot } from "react-icons/go";

function SiderLeftProfile(){
  const yearNow = new Date().getFullYear();
  const [styleSide, setStyleSider] = useState(false);
  const refSiderLeft = useRef();

  useEffect(() => {
    document.addEventListener('scroll', fixedOptionHead, false);

    return () => document.removeEventListener('scroll', removeFixedOptionHead, false)
  })

  const fixedOptionHead = () => {
    let curentRef = refSiderLeft.current
    if(curentRef){
      if(window.scrollY - 520 > 0){
        setStyleSider(true)
      }else {
        setStyleSider(false)
      }
    }
  }

  const removeFixedOptionHead = () => {
     setStyleSider(false)
  }

  return(
    <>
      <Col span={6}>
        <div className={`sider-profile`} ref={refSiderLeft} style={styleSide ? {position: 'fixed', top: '134px', width: '285px'} : null}>
          <div className="profile-story">
            <h6><GiEarthAmerica />Giới thiệu</h6>
            <div className="profile-story-text">
              Tôi là Nguyễn Văn Phước
            </div>
            <button>Chỉnh sửa tiểu sử</button>
          </div>
          <div className="profile-about">
            <ul>
              <li>
                <GiEarthAmerica />
                <span>Php develover tại Công ty Cổ Phần SOC</span>
              </li>
              <li>
                <GiEarthAmerica />
                <span>Làm việc tại Facebook Marketplace Assistant</span>
              </li>
              <li>
                <GiEarthAmerica />
                <span>Sống tại Thành phố Hồ Chí Minh</span>
              </li>
              <li>
                <GiEarthAmerica />
                <span>Có 1.855 người theo dõi</span>
              </li>
              <li>
                <GiEarthAmerica />
                <span>CHỦ TK: NGUYEN VAN PHUOC</span>
              </li>
              <li>
                <GiEarthAmerica />
                <span>VIETCOMBANK: 0911000052165</span>
              </li>
            </ul>
            <button>Chỉnh sửa chi tiết</button>
          </div>
          <div className="box-footer">
              <ul>
                  <li><GoPrimitiveDot />
                      <Link to="privacy">Quyền riêng tư</Link>
                  </li>
                  <li><GoPrimitiveDot />
                      <Link to="rules-mohi">Điều khoản</Link>
                  </li>
                  <li><GoPrimitiveDot />
                      <Link to="privacy-policy">Chính sách bảo mật</Link>
                  </li>
                  <li><GoPrimitiveDot />
                      <Link to="help">Trợ giúp</Link>
                  </li>
              </ul>
              <div className="brand-app">Mohi &copy; {yearNow}</div>
          </div>
          </div>
      </Col>
    </>
  )
}

export default SiderLeftProfile;
