import React, {useState, useEffect, useRef} from  'react';
import {Link} from 'react-router-dom';
import { AiFillCamera } from "react-icons/ai";
import { BsThreeDots, BsPencil } from "react-icons/bs";
import {PUBLIC_URL} from "./../../../Constants/public.jsx";

function HeaderProfile(){
  const refHeader = useRef();
  const [toggleSpan, setToggleSpan] = useState();
  const [toggleTopLeft, setToggleTopLeft] = useState();
  const [styleHeader, setStyleHeader] = useState(false);
  const currentUser = JSON.parse(localStorage.getItem('ustk')).info;

  useEffect(() => {
    document.addEventListener('scroll', fixedOptionHead, false);

    return () => document.removeEventListener('scroll', removeFixedOptionHead, false)
  })

  const fixedOptionHead = () => {
    let curentRef = refHeader.current
    if(curentRef){
      if(window.scrollY - 464 > 0){
        setStyleHeader(true)
      }else {
        setStyleHeader(false)
      }
    }
  }

  const removeFixedOptionHead = () => {
     setStyleHeader(false)
  }

  function handleShow(){
    setToggleSpan({
      visibility: 'visible'
    })
    setToggleTopLeft({
      background: 'rgba(0,0,0,0.3)',
      border: '2px solid white ',
      borderRadius:'3px'
    })
  }

  function handleHide(){
    setToggleSpan({
      visibility: 'hidden'
    })
    setToggleTopLeft({
        background: 'none',
        border: '2px solid transparent ',
    })
  }
  return(
    <>
      <div className="profile-header">
        <div className="profile-header-container">
          <div
            className="profile-header-cover"
            onMouseOver={() => handleShow()}
            onMouseOut={() => handleHide()}
          >
            <div className="profile-header-cover-container">
              <div className="profile-header-cover-img">
                <img src={currentUser.user_cover} alt={currentUser.user_first_name + ' ' + currentUser.user_last_name} />
              </div>
            </div>
            <div className="profile-header-cover-top-left">
              <label htmlFor="update-cover" style={toggleTopLeft}>
              <AiFillCamera
              />
              <span style={toggleSpan}>Cập nhập ảnh bìa</span>
              </label>
              <input type="file" id="update-cover"/>
            </div>
            <div className="profile-header-cover-bottom-right">
              <div className="button-edit-profile-link">
                <button type="submit">Chỉnh sửa trang cá nhân <BsPencil /></button>
              </div>
              <div className="button-view-history-action-profile">
                <Link to="/profile/activity-log"><button type="submit">Nhật kí hoạt động <BsThreeDots /></button></Link>
              </div>
            </div>
          </div>
          <div className="profile-header-timeline">
            <div className="profile-header-avatar">
              <div className="profile-header-avatar_">
                <div className="profile-header-avatar-container">
                  <div className="profile-header-avatar-img">
                      <img src={currentUser.user_avatar} alt={currentUser.user_first_name + ' ' + currentUser.user_last_name} />
                  </div>
                </div>
                <div className="profile-header-avatar-select">
                  <div className="profile-header-avatar-select-active">
                    <div className="profile-header-avatar-update">
                    <label htmlFor="update-avatar">
                    <AiFillCamera
                    />
                    <div>Cập nhật</div>
                    </label>
                    <input type="file" id="update-avatar"/>
                    </div>
                  </div>
                </div>
              </div >
              <div className="profile-header-name">
                <Link to={'/' + currentUser.user_username}>{currentUser.user_first_name + ' ' + currentUser.user_last_name}</Link>
              </div>
            </div>
            <div className="profile-header-timeline-container" ref={refHeader} style={styleHeader ? {position: 'fixed', top: '64px', zIndex: 1000, background: 'white', width: '1140px'} : null}>
              <ul className="profile-header-navigation">
                <li>
                  <Link to={'/' + currentUser.user_username + '/timeline'}>Dòng thời gian</Link>
                </li>
                <li>
                  <Link to={'/' + currentUser.user_username + '/about'}>Giới thiệu</Link>
                </li>
                <li>
                  <Link to={'/' + currentUser.user_username + '/gallery'}>Ảnh</Link>
                </li>
                <li>
                  <Link to={'/' + currentUser.user_username + '/friends'}>Bạn bè</Link>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default HeaderProfile;
