import React, {useEffect} from 'react';
import Profile from './../Components/Profiles/MyProfile/Index.jsx';
import {useDispatch} from 'react-redux';
import axios from 'axios';
import {useParams, useSelector} from 'react-router-dom';
import {requestPostsProfile} from './../Actions/index.jsx';
import {responsePostsProfile} from './../Actions/index.jsx';

function ProfileContainer(){
  const dispatch = useDispatch();
  const {idProfile} = useParams();
  const { currentPage } = useSelector(state => state.posts);

  const fetchPostsProfile = () => {
    dispatch(requestPostsProfile())

    axios.get('/api/auth/post-profile/'+ idProfile)
    .then((res) => {
      dispatch(responsePostsProfile(res.data))
    }).catch((e) => {
      console.log(e);
    })
  }

  useEffect(() => {
    fetchPostsProfile()
  }, [])

  return(
    <Profile />
  )
}

export default ProfileContainer;
