import * as Types from './../Constants/ActionTypes.jsx';

const initialState = [];

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.POSTS:
    console.log(action);
      state = prevListPost => {
          return [...new Set([...prevListPost, action.posts])]
        }
      return [...state]
      break;
    default: return [...state]
  }
}
