import React, {useRef} from 'react';
import {Link} from 'react-router-dom';
import {BsThreeDots} from "react-icons/bs";
import CommentChild from './CommentParent.jsx';

function CommentParent(props) {
  let comment = props.comment;
  let listChildComment = props.listChildComment;
  const inputCommentParentReplyRef = useRef();
  const inputCommentChildReplyRef = useRef();

  const appendInputParentReply = () => {
    let currentRef = inputCommentParentReplyRef.current;
    console.log(inputCommentParentReplyRef);
  }

  const appendInputChildReply = () => {
    let currentRef = inputCommentChildReplyRef.current;

  }

  return (<div className="post-comment-item" ref={inputCommentParentReplyRef}>
    <div className="post-comment-item-parent">
      <div className="post-comment-item-parent-avatar">
        <Link to="">
          <img src={comment.commentAvatar} alt={comment.commentUser}/>
        </Link>
      </div>
      <div className="post-comment-item-parent-info">
        <div className="post-comment-item-parent-info-user">
          <Link to="">{comment.commentUser}</Link>
        </div>
        <div className="post-comment-item-parent-content">
          <p>{comment.commentContent}</p>
          <span><BsThreeDots/></span>
        </div>
        <div className="post-comment-item-parent-action">
          <span onClick={appendInputParentReply}>Trả lời</span>
          <span>{comment.commentCreated}</span>
        </div>
      </div>
    </div>
    {
      listChildComment.map((commentChild, indexChild) => {
        if (commentChild.commentParentId === comment.id) {
          return (<div key={indexChild} className="post-comment-item-child" ref={inputCommentChildReplyRef}>
            <div className="post-comment-item-parent-avatar">
              <Link to="">
                <img src={commentChild.commentAvatar} alt={commentChild.commentUser}/>
              </Link>
            </div>
            <div className="post-comment-item-parent-info">
              <div className="post-comment-item-parent-info-user">
                <Link to="">{commentChild.commentUser}</Link>
              </div>
              <div className="post-comment-item-parent-content">
                <p>{commentChild.commentContent}</p>
                <span><BsThreeDots/></span>
              </div>
              <div className="post-comment-item-parent-action">
                <span onClick={appendInputChildReply}>Trả lời</span>
                <span>{commentChild.commentCreated}</span>
              </div>
            </div>
          </div>)
        }
      })
    }
  </div>)
}

export default CommentParent;
