import React, {useState} from 'react';

export const Button = (props) => {
  const [isShow, setIsShow] = useState(false);

  const requestAdd = (id) => {
    setIsShow(true)
    props.addFriend(id)
  }

  return(
    <React.Fragment>
      {isShow ? <button onClick={() => requestAdd(props.id)}>Đã gửi lời mời</button> : <button onClick={requestAdd}>Thêm bạn bè</button>}
    </React.Fragment>
  )
}
