import 'draft-js-mention-plugin/lib/plugin.css';
import style from 'styled-components';
import React, { Component } from 'react';
import { EditorState, convertToRaw } from 'draft-js';
import Editor from 'draft-js-plugins-editor';
import createMentionPlugin, { defaultSuggestionsFilter } from 'draft-js-mention-plugin';
import editorStyles from './editorStyles.css';
import { connect } from "react-redux";

const mentions = [
  {
    name: 'Matthew Russell',
    link: 'https://twitter.com/mrussell247',
    avatar: 'https://pbs.twimg.com/profile_images/517863945/mattsailing_400x400.jpg',
  },
  {
    name: 'Julian Krispel-Samsel',
    link: 'https://twitter.com/juliandoesstuff',
    avatar: 'https://avatars2.githubusercontent.com/u/1188186?v=3&s=400',
  },
  {
    name: 'Jyoti Puri',
    link: 'https://twitter.com/jyopur',
    avatar: 'https://avatars0.githubusercontent.com/u/2182307?v=3&s=400',
  },
  {
    name: 'Max Stoiber',
    link: 'https://twitter.com/mxstbr',
    avatar: 'https://pbs.twimg.com/profile_images/763033229993574400/6frGyDyA_400x400.jpg',
  },
  {
    name: 'Nik Graf',
    link: 'https://twitter.com/nikgraf',
    avatar: 'https://avatars0.githubusercontent.com/u/223045?v=3&s=400',
  },
  {
    name: 'Pascal Brandt',
    link: 'https://twitter.com/psbrandt',
    avatar: 'https://pbs.twimg.com/profile_images/688487813025640448/E6O6I011_400x400.png',
  },
];

const StyleEditorMention = style.div`
  width: 100%;
  max-height: 200px;
  overflow-y: auto;
  font-size: 14px;
  padding: 5px 10px;
  margin-top: 10px;
  border: 0.5px solid rgba(0,0,0,0.1);
  border-radius:30px;
`;

 class SimpleMentionEditor extends Component {

  constructor(props) {
    super(props);
    this.mentionPlugin = createMentionPlugin();
  }

  state = {
    editorState: EditorState.createEmpty(),
    suggestions: this.props.friends,
  };

  onChange = (editorState) => {
    this.setState({
      editorState,
    });

    let currentContent = editorState .getCurrentContent();
    let raw = convertToRaw(currentContent);
    for(let key in raw.entityMap){
      const ent = raw.entityMap[key];
      if(ent.type === 'mention'){
          this.props.showMentionList(ent.data.mention, ent.data.mention);
      }
    }
  };


  //
  // onAddMention = (mention) => {
  //   console.log(mention);
  //   this.props.showMentionList(mention);
  // }

  render() {
    const { MentionSuggestions } = this.mentionPlugin;
    const plugins = [this.mentionPlugin];
    console.log(this.state);

    return (
      <div className={editorStyles.editor}>
        <StyleEditorMention>
          <Editor
            editorState={this.state.editorState}
            onChange={this.onChange}
            plugins={plugins}
            placeholder="Gắn thẻ ai đó..."
            ref={(element) => { this.editor = element; }}
          />
        </StyleEditorMention>
        <MentionSuggestions
          suggestions={this.state.suggestions}
        />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    friends: state.friends.list,
  }
}

export default connect(mapStateToProps, null)(SimpleMentionEditor)
