import React, {useRef, useState} from 'react';

function VideosPage(){
  const ref = useRef();
  const [img, setImg] = useState()

  const getCurrentTime = () => {
    setImg(capture(ref.current))
  }

  const dataURLtoFile = (dataurl, filename) => {
    var arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)[1],
      bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
  }

  const capture = (video) => {
    let w = video.videoWidth ;
    let h = video.videoHeight ;
    let canvas = document.createElement('canvas');
    canvas.width = w;
    canvas.height = h;
    let ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, w, h);
    console.log(canvas);
    let dataURI = canvas.toDataURL('image/jpeg');
    // let a =
    let boj={
      canvas:canvas,
      file:dataURLtoFile(dataURI, `${+new Date()}.jpg`),
      base64:dataURI
    }
    console.log(boj);
    return boj.base64;
  }

  return(
    <div className="filter-video-container">
      <div className="filter-video-title">
        <span>Video</span>
      </div>
      <div className="filter-video">
        <div className="filter-video-item">
          <video
            ref={ref}
            src={"https://res.cloudinary.com/mohi-vn/video/upload/v1605524086/page/cover/mov_bbb_rker0g.ogv"}
            poster="https://tse1.mm.bing.net/th?id=OIP.BoJfthnaI5oOTBP6GmrZrAHaEK&pid=Api&P=0&w=326&h=184src="
            controls
            crossOrigin="true"
          >
          </video>
          <button onClick={getCurrentTime}>aaaaaaaaaa</button>
          <img src={img} alt=""/>
      </div>
      </div>
    </div>
  )
}

export default VideosPage;
