import * as Types from './../Constants/ActionTypes.jsx';

const initialState = {
  currentPage: 1,
  loading: true,
  hasMore: false,
  error: false,
  posts: null
};

export const posts = (state = initialState, action) => {
  switch (action.type) {
    case Types.SET_CURRENT_PAGE:
      state = {...state.currentPage + 1}
      return {...state}
      break;
    case Types.POSTS:
      state = [...state, ...action.posts]
      return state
      break;
    case Types.MORE_POSTS:
      state = action.posts
      return [...state, action.posts]
      break;
    default: return state
  }
}
