import React, {useEffect, useState} from 'react';
import {useForm} from 'react-hook-form';
import './LoginTwo.css';
import {RiErrorWarningLine} from "react-icons/ri";

function LoginTwo(props){
  const {register, handleSubmit, errors} = useForm();
  const error = props.errorLogin ? props.errorLogin.error : '';
  const [errorLogin, setErrorLogin] = useState(error);

  const onSubmit = (data) => {
    console.log(data);
  }

  const validate = (value) => {
    if(!value){
        setErrorLogin('Vui lòng nhập tên đăng nhập hoặc mật khẩu của bạn')
      return false;
    }
    return true;
  }

  return(
    <React.Fragment>
      <div className="login-two-container">
        <div className="login-two">
          <form onSubmit={handleSubmit(onSubmit)}>
            <h2>Mohi</h2>
            <div className="login-two-message">{errorLogin}</div>
            <div className="form-login-two-group">
              <input
                type="text"
                name="user_email"
                id="code"
                placeholder="Tên đăng nhập"
                ref={register({
                  validate: value => validate(value)
                })}
                style={errors.user_email
                  ? {
                    border: '1px solid red'
                  }
                  : null}
                />
              {errors.user_email && <div className = "error_login_two" ><RiErrorWarningLine className="error_sex"/></div>}
            </div>
            <div className="form-login-two-group">
              <input
                type="text"
                name="user_password"
                id="code"
                placeholder="Mật khẩu"
                ref={register({
                  validate: value => validate(value)
                })}
                style={errors.user_password
                  ? {
                    border: '1px solid red'
                  }
                  : null}
                />
              {errors.user_password && <div className = "error_login_two" ><RiErrorWarningLine className="error_sex"/></div>}
            </div>
            <div className="form-login-two-group">
              <span className="forget-password">Quên mật khẩu?</span>
            </div>
            <div className="form-login-two-group">
              <button type="submit">Đăng nhập</button>
            </div>
          </form>
        </div>
      </div>
    </React.Fragment>
  )
}

export default LoginTwo;
