import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import'./PopupEditPage.css';
import L from "leaflet";
import * as ELG from "esri-leaflet-geocoder";
import "leaflet/dist/leaflet.css";
import "esri-leaflet-geocoder/dist/esri-leaflet-geocoder.css";
import {PUBLIC_URL} from './../../Constants/public.jsx';

function PopupEditPage(){
  const [lat, setLat] = useState(10.7553411);
  const [long, setLong] = useState(106.4150395);
  const [zoom, setZoom] = useState(5);
  const myIcon = L.icon({
    iconUrl: PUBLIC_URL + 'address_icon.png',
    iconSize: [30, 30],
    popupAnchor: [0,-10],
  });
  useEffect(() => {
    var map = L.map('map_page').setView([lat, long], zoom);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    	maxZoom: 19,
    	attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    let marker_first = L.marker([lat, long], {icon: myIcon, draggable:'true'}).addTo(map)
        .bindPopup('ReactJS Việt Nam')
        .openPopup();
    marker_first.on('dragend', function(event) {
        let marker = event.target;
        let result = marker.getLatLng();
        console.log(result);
    });
    //search and choose location
    const searchControl = new ELG.Geosearch().addTo(map);
    const results = new L.LayerGroup().addTo(map);
    searchControl.on("results", function (data) {
      console.log(data);
      results.clearLayers();
      for (let i = data.results.length - 1; i >= 0; i--) {
        results.addLayer(L.marker(data.results[i].latlng));
      }
    });

  }, []);

  const getMyAdress = () => {
    navigator.geolocation.getCurrentPosition(getLatLon);
  }

  function getLatLon(position) {
    let latitude = position.coords.latitude;
    // let longitude = position.coords.longitude;
    // let marker = L.marker(map.getCenter()).addTo(map);
    // let newLatLng = new L.LatLng(latitude,longitude);
    // marker.setLatLng(newLatLng);
    // map.setView(newLatLng, 8);
  }
  return(
    <div className="popup-edit-page">
      <div className="popup-edit-page-container">
        <div className="map-page-container">
          <h5>Chỉnh sửa vị trí cửa hàng <Link to="">ReactJS Việt Nam</Link><small>( Di chuyển vị trí có sẵn, hoặc tìm kiếm địa điểm để vài đặt lại vị trí )</small></h5>
          <div id="map_page" className="map_page"></div>
          <div className="map-page-button">
            <button type="submit" onClick={getMyAdress}>Lấy vị trí của bạn</button>
            <button type="submit">Xác nhận</button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PopupEditPage;
