import React,{ useEffect} from 'react';
import Messenger from './../Components/Messenger/index.jsx';
import axios from 'axios';
import {useDispatch} from 'react-redux';
import { fetchMessagePopup } from './../Actions/index.jsx';

function MessengerContainer(){
  const dispatch = useDispatch();
  
  const getListMessage = async () => {
    await axios.get("/api/auth/list-message").then((res) => {
      dispatch(fetchMessagePopup(res.data))
    }).catch((e) => {
      console.log(e);
    })
  }

  useEffect(() => {
    getListMessage()
  }, [])
  return(
    <Messenger />
  )
}

export default MessengerContainer;
