import React from 'react';
import {useForm} from 'react-hook-form';
import {useHistory} from 'react-router-dom';

function LoginTwo(){
  const history = useHistory();
  const {register, handleSubmit, errors} = useForm();
  const errorLogin = history.location.state ? history.location.state.error : '';
  console.log(history);
  const onSubmit = (data) => {
    console.log(data);
  }

  return(
    <React.Fragment>
      <div className="login-two-container">
        <form onSubmit={handleSubmit(onSubmit)}>
          <h2>Đăng nhập</h2>
          <div className="form-veri-code-group">
            <label htmlFor="code">Mã code:
            </label>
            <input type="text" name="code" id="code" placeholder="Nhập mã gồm 6 số..." ref={register({required: true})}/>
          </div>
          <div className="login-message">{errorLogin}</div>
          <div className="form-veri-code-group">
            <button type="submit">Xác nhận</button>
          </div>
        </form>
      </div>
    </React.Fragment>
  )
}

export default LoginTwo;
