import { BrowserRouter as Router } from 'react-router-dom';
import Home from './Home.jsx';
import LoginContainer from './../Containers/LoginContainer.jsx';
var CryptoJS = require("crypto-js");

function App() {
  let ustk_encrypt = localStorage.getItem('ustk') ? localStorage.getItem('ustk') : '';
  let bytes = CryptoJS.AES.decrypt(ustk_encrypt, 'current_user_and_token');
  let ustk = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
  console.log(ustk);
  const [isLogin, setIsLogin] = useState({
    status: false,
    token: ''
  })

  const setLoginFunc = (data) => {
    let current_user_and_token = CryptoJS.AES.encrypt(data, 'current_user_and_token').toString();
