import React, {useState, useEffect} from 'react';
import './EditPage.css';
import {Link} from 'react-router-dom';
import { Editor, EditorState } from 'draft-js';
import style from 'styled-components';

function EditPage(props){
    console.log(props);
    const editorContent = EditorState.createEmpty();
    const [editorState, setEditorState] = useState({ editorState: editorContent });

    const handleEditorChange = (editorState) => {
      setEditorState({ editorState });
    }

    const closeEditPage = () => {
      props.closeEditPage()
    }

    const StyleEditor = style.div`
      width: 500px;
      border: 1px solid rgba(0,0,0,0.1);
      border-radius: 3px;
      min-height:50px;
      max-height: 150px;
      overflow-y: auto;
    `;
    return(
      <div className="popup-edit-page-full">
        <div className="popup-edit-page-presentation" onClick={closeEditPage}>
        </div>
        <div className="popup-edit-page-container">
          <div className="popup-edit-page-content">
            <div className="popup-edit-form-group">
              <label>Chỉnh sửa mô tả của <Link to="">ReactJS Việt Nam</Link></label>
              <StyleEditor>
                <Editor
                  placeholder="Nhập mô tả..."
                  editorState={editorState.editorState}
                  onChange={handleEditorChange}
                />
            </StyleEditor>
            </div>
            <div className="popup-edit-form-group">
              <button type='submit'>Lưu</button>
              <button type='submit'>Đóng</button>
            </div>
          </div>
        </div>
      </div>
    )
  }

export default EditPage;
