import React, { useState } from 'react';
import { FaUserAlt } from "react-icons/fa";
import { AiOutlinePlusCircle, AiFillEdit } from "react-icons/ai";
import { MdDelete } from "react-icons/md";
import { FcSettings } from "react-icons/fc";
import { Link, Route, Switch, useLocation, useParams } from 'react-router-dom';
import { Tooltip, Spin } from 'antd';
import { useDispatch } from 'react-redux';
import { toggleStatusPresentialModal } from './../../../Actions/index.jsx';
import { useSelector } from 'react-redux';

const OverView = (props) => {

  const dispatch = useDispatch();

  function addWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_work_space'));
  }
  function addStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_add_study_space'));
  }
  function addLiveSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_live_space'));
  }
  const onSubmitWorkSpace = (data) => {
    props.onSubmitWorkSpace(data);
  }

  return (
    <div className="about-content-right-overview">
      <div className="about-content-right-first">
        <div className="about-content-right-first-place-work">
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addWorkSpace}>
              <span >Thêm một nơi làm việc</span>
            </div>
          </div>
        </div>
        <div className="about-content-right-first-place-study" onClick={addStudySpace} >
          Nơi đang học
        </div>
        <div className="about-content-right-first-place-live" onClick={addLiveSpace}>
          NƠi sống
        </div>
      </div>
      <div className="about-content-right-second">
        <ul>
          <li>
            0365610561
          </li>
          <li>
            phuocnv106@gmail.com
          </li>
          <li>
            thông tin khác
          </li>
          <li>
            05/02/2000
          </li>
          <li>
            <Link to={"/profile/" + props.idProfile + "/about/contact-info"}><span>Chỉnh sửa thông tin liên hệ và cơ bản</span></Link>
          </li>
        </ul>
      </div>
    </div>
  )
}


const Education = () => {

  const dispatch = useDispatch();

  function addWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_add_work_space'));
  }
  function editWorkSpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_work_space'));
  }
  function addStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_add_study_space'));
  }
  function editStudySpace() {
    dispatch(toggleStatusPresentialModal('modal_edit_study_space'));
  }
  function addStudyHighSchool() {
    dispatch(toggleStatusPresentialModal('modal_add_study_high_school'));
  }
  function editStudyHighSchool() {
    dispatch(toggleStatusPresentialModal('modal_edit_study_high_school'));
  }



  const ButtonOptionWork = () => {

    return (
      <React.Fragment>
<div className="button-option">
          <span onClick={editWorkSpace}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }
  const ButtonOptionStudy = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editStudySpace}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete />Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  const ButtonOptionStudyHighSchool = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editStudyHighSchool}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete />Xóa</span>
        </div>
      </React.Fragment>
    )

  }


  return (
    <>
      <div className="about-content-education">
        <div className="job">
          <h6>Công việc</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addWorkSpace}>
              <span>Thêm một nơi làm việc</span>
            </div>
          </div>
          <div className="job-item" >
            Công ty cổ phần SOC
            <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionWork}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>

        </div>
        <div className="collegs">
          <h6>Cao đẳng/đại học</h6>
          <div className="add-something">
            <div className="icon-add" >
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addStudySpace}>
              <span>Thêm một trường cao đẳng/đại học</span>
            </div>
          </div>
          <div className="collegs-item">
            Cao đẳng FPT
            <Tooltip trigger="click" placement="rightTop" title={ButtonOptionStudy}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
        <div className="school">
          <h6>Trung học</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addStudyHighSchool}>
              <span>Thêm một trường trung học</span>
            </div>
          </div>
          <div className="school-item">
            Trung học phổ thông Nguyễn Huệ - Dăk Doa - Gia Lai
            <Tooltip trigger="click" placement="rightBottom" title={ButtonOptionStudyHighSchool}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>

        </div>
      </div>
    </>
  )
}

const ContactInfo = () => {
  const dispatch = useDispatch();

  function addEmail() {
dispatch(toggleStatusPresentialModal('modal_add_email'));
  }
  function editEmail() {
    dispatch(toggleStatusPresentialModal('modal_edit_email'));
  }
  function addPhone() {
    dispatch(toggleStatusPresentialModal('modal_add_phone'));
  }
  function editPhone() {
    dispatch(toggleStatusPresentialModal('modal_edit_phone'));
  }
  function addWebsite() {
    dispatch(toggleStatusPresentialModal('modal_add_website'));
  }
  function editWebsite() {
    dispatch(toggleStatusPresentialModal('modal_edit_website'));
  }

  const ButtonOptionEmail = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editEmail}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  const ButtonOptionPhone = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editPhone}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  const ButtonOptionWebsite = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editWebsite}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  return (
    <>
      <div className="about-content-education">
        <div className="job">
          <h6>Email</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addEmail}>
              <span>Thêm một email</span>
            </div>
          </div>
          <div className="job-item">
            phuocnv106@gmail.com
            <Tooltip trigger="click" placement="rightTop" title={ButtonOptionEmail}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
        <div className="collegs">
          <h6>Số điện thoại</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addPhone}>
              <span>Thêm một số điện thoại</span>
            </div>
          </div>
          <div className="collegs-item">
            0365610561
            <Tooltip trigger="click" placement="rightTop" title={ButtonOptionPhone}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
        <div className="school">
          <h6>Website</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addWebsite}>
<span>Thêm một link website</span>
            </div>
          </div>
          <div className="school-item">
            ppmedia.xyz
            <Tooltip trigger="click" placement="right" title={ButtonOptionWebsite}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
      </div>
    </>
  )
}

const Bio = () => {

  const dispatch = useDispatch();

  function addIntroduce() {
    dispatch(toggleStatusPresentialModal('modal_add_introduce'));
  }
  function editIntroduce() {
    dispatch(toggleStatusPresentialModal('modal_edit_introduce'));
  }
  function addNickName() {
    dispatch(toggleStatusPresentialModal('modal_add_nick_name'));
  }
  function editNickName() {
    dispatch(toggleStatusPresentialModal('modal_edit_nick_name'));
  }

  const ButtonOptionIntroduce = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editIntroduce}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  const ButtonOptionNickName = () => {

    return (
      <React.Fragment>
        <div className="button-option">
          <span onClick={editNickName}><AiFillEdit /> Chỉnh sửa</span>
          <span><MdDelete /> Xóa</span>
        </div>
      </React.Fragment>
    )

  }

  return (
    <>
      <div className="about-content-education">
        <div className="job">
          <h6>Giới thiệu về bản thân</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addIntroduce} >
              <span>Viết vài điều về bản thân bạn</span>
            </div>
          </div>
          <div className="job-item">
            tôi là developer
            <Tooltip trigger="click" placement="rightTop" title={ButtonOptionIntroduce}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
        <div className="collegs">
          <h6>Các tên khác</h6>
          <div className="add-something">
            <div className="icon-add">
              <AiOutlinePlusCircle />
            </div>
            <div className="title-add" onClick={addNickName}>
              <span>Thêm một biệt danh hoặc tên khai sinh của bạn</span>
            </div>
          </div>
          <div className="collegs-item">
            Rin <span>
            <small>(Hiển thị ở đầu trang cá nhân)</small></span>
            <Tooltip trigger="click" placement="rightTop" title={ButtonOptionNickName}>
              <span className="edit-work-space-button"><FcSettings /></span>
            </Tooltip>
          </div>
        </div>
      </div>
    </>
  )
}

function About({ profile }) {
  const {idProfile} = useParams();
  const path = useLocation().pathname.split('/');
  const isActive = path[4];
  const { loading, info } = useSelector(state => state.profile);

  if(loading && !info){
    return(
      <div className="about-container" style={{display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '200px'}}>
        <Spin />
      </div>
    )
  }

  console.log(info);
  return (
    <>
      <div className="about-container">
        <div className="about-header">
          <FaUserAlt /><h5>Giới thiệu</h5>
        </div>
        <div className="about-content">
          <div className="about-content-left">
            <ul>
              <Link to={"/profile/" + idProfile + "/about/overview"}>
                <li className={isActive === 'overview' || !isActive ? 'active' : ''}>
                  <span>Tổng quan</span>
                </li>
              </Link>
              <Link to={"/profile/" + idProfile + "/about/education"}>
                <li className={isActive === 'education' ? 'active' : ''}>
                  <span>Công việc và học vấn</span>
                </li>
              </Link>
              <Link to={"/profile/" + idProfile + "/about/contact-info"}>
                <li className={isActive === 'contact-info' ? 'active' : ''}>
                  <span>Thông tin liên hệ và cơ bản</span>
                </li>
              </Link>
              <Link to={"/profile/" + idProfile + "/about/bio"}>
                <li className={isActive === 'bio' ? 'active' : ''}>
                  <span>Chi tiết về bạn</span>
                </li>
              </Link>
            </ul>
          </div>
          <div className="about-content-right">
            <Switch>
              <Route path="/profile/:idProfile/about/overview"><OverView idProfile={idProfile} /></Route>
              <Route path="/profile/:idProfile/about/education" ><Education /></Route>
              <Route path="/profile/:idProfile/about/contact-info" ><ContactInfo /></Route>
              <Route path="/profile/:idProfile/about/bio" ><Bio /></Route>
              <Route><OverView idProfile={idProfile} /></Route>
            </Switch>
          </div>
        </div>
      </div>
    </>
  )
}

export default About;
