import React from 'react';
import SiderProfile from './SiderProfile.jsx';
import Post from './Post.jsx';
import { Row, Col } from 'antd';
import CreatePost from './CreatePost.jsx';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

function TimeLine({ myPosts }) {
  const { listPosts, loading, error, hasMore, loadingAddComment } = useSelector(state => state.myPosts);

  const submitCreatePost = async (valueEditor, fileList, privacyNewFeed, privacyStory, placeNewFeed, placeStory, listMention) => {

    let mention = [];
    listMention.map(m => {
      mention.push(m.data.mention.id)
    })
    let tags = mention.filter((value, index, mention) => mention.indexOf(value) === index);
    let tag = tags.toString();

    let data = {
      placeStory: placeStory,
      placePost: placeNewFeed,
      post_Content: valueEditor,
      post_Privacy: privacyNewFeed,
      tag_users_UserId: tag,
      story_Privacy: privacyStory,
      post_images_Url: fileList
    }

    await axios.post('/api/auth/create-post-profile', data).then((res) => {
      console.log(res);
    }).catch((e) => {
      console.log(e);
    })

  }
  return (
    <>
      <Row>
        <SiderProfile />
        <Col span={16} style={{ paddingLeft: '20px' }}>
          <CreatePost submitCreatePost={submitCreatePost}/>
          {listPosts.map((post, index) => <Post key={index} post={post} />)}
        </Col>
      </Row>
    </>
  )
}

export default TimeLine;
