import React, {useEffect, useState} from 'react';
import {AiFillPicture, AiOutlineLike} from 'react-icons/ai';
import { Link, Route, Switch, useLocation } from 'react-router-dom';

function Friends(){
  const path = useLocation().pathname.split('/');
  const isActive = path[3];
  const [isChangeAlbum, setIsChangeAlbum] = useState(true);

  useEffect(() =>{
    setIsChangeAlbum(true)
  },[isActive])

  function changeContentAlbum(){
      setIsChangeAlbum(false)
  }

  const Menu = () => {
    return(
      <div>
      a
      </div>
    )
  }

  return(
    <div className="profile-firends-container">
      <div className="galerry-head">
        <div className="galerry-title">
          <AiFillPicture /><h5>Bạn bè</h5>
        </div>
        <div className="galerry-navigation">
          <ul>
            <Link to="/friends/friends-all/overview">
              <li ><span className={isActive === 'overview' || !isActive ? 'galerry-active' : ''}>Tất cả bạn bè</span></li>
            </Link>
            <Link to="/friends/gallery/album">
              <li ><span className={isActive === 'album' ? 'galerry-active' : ''}>Đã thêm gần đây</span></li>
            </Link>
            <Link to="/friends/gallery/album">
              <li ><span className={isActive === 'album' ? 'galerry-active' : ''}>Sinh nhật</span></li>
            </Link>
            <Link to="/friends/gallery/album">
              <li ><span className={isActive === 'album' ? 'galerry-active' : ''}>Thành phố hiện tại</span></li>
            </Link>
            <Link to="/friends/gallery/album">
              <li ><span className={isActive === 'album' ? 'galerry-active' : ''}>Người theo dõi</span></li>
            </Link>
            <Link to="/friends/gallery/album">
              <li ><span className={isActive === 'album' ? 'galerry-active' : ''}>Đang theo dõi</span></li>
            </Link>
          </ul>
        </div>
      </div>
      <div className="galerry-content">
        <Switch>
          <Route path="/friends/gallery/overview" component={Menu}/>
        </Switch>
      </div>
    </div>
  )



}

export default Friends;
