import React from 'react';
import { FiPlus } from "react-icons/fi";
import { AiOutlineClose } from "react-icons/ai";

export default function RenderFile(props){
  const deleteFile = (index) => {
    props.deleteFile(index)
  }
  return(
  <React.Fragment>
    {props.files.map((photo, index) => {
      return (
          <div className="list-preview-item" key={index}>
            <img src={photo} />
          <div className="list-preview-item-presential"><AiOutlineClose onClick={() => deleteFile(index)}/></div>
          </div>

      )
    })}
    <div className="list-preview-next-item" >
      <label htmlFor="create_post_upload_image"><FiPlus/></label>
    </div>
  </React.Fragment>
  )
}
