import React, { Component } from "react";
import Editor, { createEditorStateWithText } from "draft-js-plugins-editor";
import createEmojiPlugin from "draft-js-emoji-plugin";
import "draft-js-emoji-plugin/lib/plugin.css";
import { Modifier, EditorState } from 'draftjs';
import style from 'styled-components';
import htmlToDraft from 'html-to-draftjs';

const emojiPlugin = createEmojiPlugin({
  useNativeArt: true
});
const { EmojiSuggestions, EmojiSelect } = emojiPlugin;
const plugins = [emojiPlugin];

const StyleEditor = style.div`
  width: 100%;
  max-height: 100px;
  overflow-y: auto;
  border: 1px solid #ddd;
  margin-left: 10px;
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 30px;
`;

const Wrapper = style.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const StyleEmoji = style.div`
  position: absolute;
  right: 10px;
  border: 0;
  z-index: 2;
`;

export default class CommentEditor extends Component {
  constructor(props){
    super(props)
    const text = '';
    this.state = {
      editorState: createEditorStateWithText(text)
    };
  }

  componentDidUpdate(){
    if(this.state.idUserReceivedReply !== this.props.idUserReceivedReply || this.state.userReceivedReply !== this.props.userReceivedReply){
      let userLink = "<a href=''>"+ this.props.userReceivedReply +"</a>";
      let { contentBlocks, entityMap } = htmlToDraft(userLink);
      let contentState = Modifier.replaceWithFragment(
         editorState.getCurrentContent(),
         editorState.getSelection(),
         ContentState.createFromBlockArray(contentBlocks, entityMap).getBlockMap()
       )

      EditorState.push(this.state.editorState, contentState, 'insert-fragment');
    }
  }
  onChange = (editorState) => {
    this.setState({
      editorState
    });
  };

  focusCommentInput(){
    this.commentInput.focus();
  }

  render() {
    return (
      <Wrapper>
        <StyleEditor>
          <Editor
            editorState={this.state.editorState}
            onChange={this.onChange}
            plugins={plugins}
            placeholder="Viết bình luận..."
            ref={(element) => {
              this.commentInput = element;
            }}
          />
        </StyleEditor>
        <EmojiSuggestions />
        <StyleEmoji>
          <EmojiSelect />
        </StyleEmoji>
      </Wrapper>
    );
  }
}
