import React from 'react';
import {AiFillPicture} from 'react-icons/ai';
import {FaCheck} from 'react-icons/fa';
import {BsSearch} from 'react-icons/bs';
import { Link, Route, Switch, useLocation } from 'react-router-dom';
import {Row, Col} from 'antd';
import {useSelector, useDispatch} from 'react-redux';
import {searchFriendProfile} from './../../../Actions/index.jsx';

function Friends({friends}){
  const path = useLocation().pathname.split('/');
  const isActive = path[3];
  const dispatch = useDispatch();
  const {friendOfProfile} = useSelector(state => state.friends)
  const {list} = useSelector(state => state.friends)
  const currentUser = JSON.parse(localStorage.getItem('ustk')).info;

  const onSearchChange = (e) => {
    console.log(e.target.value);
    dispatch(searchFriendProfile(e.target.value))
  }

  const FriendsAll = () => {
    return(
      <div className="friends-all">
      <Row>
        {friendOfProfile.map((friends, index) => {
          return(
            <Col span={12} key={index}>
              <div className="friend-item-container">
                <div className="friend-item-left">
                  <div className="friend-item-avatar">
                    <Link to={'/profile/' + friends.user_username}>
                      <img src={friends.user_avatar} alt={friends.user_first_name + ' ' + friends.user_last_name} />
                    </Link>
                  </div>
                  <div className="friend-item-info">
                    <div className="friend-item-name">
                      <Link to={'/profile/' + friends.user_username}>
                      {friends.user_first_name + ' ' + friends.user_last_name}
                      </Link>
                    </div>
                    <div className="friend-item-desc">
                      {friends.user_introduce}
                    </div>
                  </div>
                </div>
                {friends.id !== currentUser.id &&
                  <div className="friend-item-right">
                    {list.filter(f=> f.id === friends.id).length ?
                      <button><FaCheck /> Bạn bè</button>
                    :
                      <button><FaCheck /> Thêm bạn bè</button>
                    }
                  </div>
                }
              </div>
            </Col>
          )
        })}
      </Row>
    </div>
    )
  }

  return(
    <div className="profile-firends-container">
      <div className="galerry-head">
        <div className="galerry-title">
          <AiFillPicture /><h5> Bạn bè</h5>
        </div>
        <div className="galerry-navigation">
          <ul>
              <li ><span className={isActive === 'friends-all' || !isActive ? 'galerry-active' : ''}>Tất cả bạn bè</span></li>
            <span className="search-friend">
              <input name="search_friend" placeholder="Tìm kiếm bạn bè" type="text" onChange={onSearchChange}/>
              <button ><BsSearch /></button>

            </span>
            <div className="clear"></div>
          </ul>
        </div>
      </div>
      <div className="galerry-content">
        <Switch>
          <Route component={FriendsAll}/>
        </Switch>
      </div>
    </div>
  )



}

export default Friends;
